mapper.select:

select 

from h_user_info
where user_mng_number = #{userMngNumber,jdbcType=CHAR}
  and revision_number = #{revisionNumber,jdbcType=DECIMAL}

mapper.delete:

delete from h_user_info
where user_mng_number = #{userMngNumber,jdbcType=CHAR}
  and revision_number = #{revisionNumber,jdbcType=DECIMAL}
